package com.xmy.blog.dao;import com.xmy.blog.po.Tag;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import java.util.List;/** * @author xmy * @date 2021/3/16 7:22 下午 */public interface TagRepository extends JpaRepository<Tag,Long> {    Tag findByName(String name);    @Query("select t from Type t")    List<Tag> findTop(Pageable pageable);}