package com.xmy.blog.dao;import com.xmy.blog.po.Type;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import java.util.List;/** * @author xmy * @date 2021/3/15 2:07 下午 */public interface TypeRepository extends JpaRepository<Type,Long> {    Type findByName(String name);    @Query("select t from Type t")    List<Type> findTop(Pageable pageable);}