package com.xmy.blog.entity;import javax.validation.constraints.NotBlank;import java.util.ArrayList;import java.util.List;/** * @author xmy * @date 2021/3/8 11:38 下午 */public class Tag {    private Long id;    @NotBlank(message="标签名称不能为空")    private String name;    private List<Blog> blogs = new ArrayList<>();    public Tag() {    }    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public List<Blog> getBlogs() {        return blogs;    }    public void setBlogs(List<Blog> blogs) {        this.blogs = blogs;    }    @Override    public String toString() {        return "Tag{" +                "id=" + id +                ", name='" + name + '\'' +                '}';    }}