package com.xmy.blog.interceptor;import org.springframework.web.servlet.handler.HandlerInterceptorAdapter;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;/** * @author xmy * @date 2021/3/15 11:24 上午 *///登陆拦截器，在未登录时，不能直接通过/blogs访问到blog的管理界面public class LoginInterceptor extends HandlerInterceptorAdapter {    @Override    public boolean preHandle(HttpServletRequest request, HttpServletResponse response, Object handler) throws Exception {        if (request.getSession().getAttribute("user") == null) {            response.sendRedirect("/admin");//重定向到登陆界面            return false;        }        return true;    }}