package com.xmy.blog.interceptor;import org.springframework.context.annotation.Configuration;import org.springframework.web.bind.support.WebExchangeBindException;import org.springframework.web.servlet.config.annotation.InterceptorRegistry;import org.springframework.web.servlet.config.annotation.WebMvcConfigurer;import org.springframework.web.servlet.config.annotation.WebMvcConfigurerAdapter;/** * @author xmy * @date 2021/3/15 11:27 上午 */@Configurationpublic class WebConfig implements WebMvcConfigurer {    @Override    public void addInterceptors(InterceptorRegistry registry) {        registry.addInterceptor(new LoginInterceptor())                .addPathPatterns("/admin/**")                .excludePathPatterns("/admin")                .excludePathPatterns("/admin/login");    }}