package com.xmy.blog.po;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.Id;import javax.persistence.Table;/** * @author xmy * @date 2021/3/8 11:38 下午 */@Entity@Table(name = "t_tag")public class Tag {    @Id    @GeneratedValue    private Long id;    private String name;    public Tag() {    }    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    @Override    public String toString() {        return "Tag{" +                "id=" + id +                ", name='" + name + '\'' +                '}';    }}