package com.xmy.blog.service;import com.xmy.blog.dao.UserRepository;import com.xmy.blog.po.User;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import util.MD5Utils;/** * @author xmy * @date 2021/3/9 11:28 下午 */@Servicepublic class UserServiceImpl implements UserService {    @Autowired    private UserRepository userRepository;    @Override    public User userCheck(String username, String password) {        User user = userRepository.findByUsernameAndPassword(username, MD5Utils.code(password));        return user;    }}