package com.xmy.blog.service.impl;import com.xmy.blog.dao.TypeDao;import com.xmy.blog.entity.Type;import com.xmy.blog.service.TypeService;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.List;/** * @author xmy * @date 2021/3/15 2:06 下午 */@Servicepublic class TypeServiceImpl implements TypeService {    private TypeDao typeDao;    @Transactional    @Override    public int saveType(Type type) {        return typeDao.saveType(type);    }    @Transactional    @Override    public Type getType(Long id) {        return typeDao.getType(id);    }    @Transactional    @Override    public List<Type> getAllType() {        return typeDao.getAllType();    }    @Transactional    @Override    public List<Type> getAllTypeAndBlog() {        return typeDao.getAllTypeAndBlog();    }    @Transactional    @Override    public Type getTypeByName(String name) {        return typeDao.getTypeByName(name);    }    @Transactional    @Override    public int updateType(Type type) {        return typeDao.updateType(type);    }    @Transactional    @Override    public void deleteType(Long id) {        typeDao.deleteType(id);    }}