package com.xmy.blog.service.impl;import com.xmy.blog.dao.UserDao;import com.xmy.blog.entity.User;import com.xmy.blog.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import util.MD5Utils;/** * @author xmy * @date 2021/3/23 4:39 下午 */@Servicepublic class UserServiceImpl implements UserService {    @Autowired    private UserDao userDao;    @Override    public User checkUser(String username, String password) {        User user = userDao.findByUsernameAndPassword(username, MD5Utils.code(password));        return user;    }}