package com.xmy.blog.vo;/** * @author xmy * @date 2021/3/17 7:27 下午 *///为了解决空指针的问题public class BlogQuary {    private String title;    private Long typeId;    private boolean recommend;    public BlogQuary() {    }    public BlogQuary(String title, Long typeId, Boolean recommend) {        this.title = title;        this.typeId = typeId;        this.recommend = recommend;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public Long getTypeId() {        return typeId;    }    public void setTypeId(Long typeId) {        this.typeId = typeId;    }    public boolean isRecommend() {        return recommend;    }    public void setRecommend(boolean recommend) {        this.recommend = recommend;    }}